use std::cmp::Ordering;
use std::fmt;
use std::ops::{Add, Mul, Sub};
mod internals;
mod sign;
mod utils;
use crate::internals::BigInt;
use sign::Sign;

impl PartialOrd for BigInt {
    fn partial_cmp(&self, other: &Self) -> Option<Ordering> {
        if self.sign < other.sign {
            return Some(Ordering::Less);
        }

        if self.sign > other.sign {
            return Some(Ordering::Greater);
        }

        if Sign::Zero == self.sign && Sign::Zero == other.sign {
            return Some(Ordering::Equal);
        }

        let comparation = self.abs_cmp(other);
        return Some(if self.sign == Sign::Positive {
            comparation?
        } else {
            comparation?.reverse()
        });
    }
}

impl fmt::Display for BigInt {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        write!(f, "{}", self.to_owned_string())
    }
}

impl Add for BigInt {
    type Output = Self;

    fn add(self, rhs: Self) -> Self::Output {
        if self.sign == Sign::Zero {
            return rhs;
        }
        if rhs.sign == Sign::Zero {
            return self;
        }
        if self.sign == rhs.sign {
            return self.add_same_sign(rhs);
        }
        self.add_diff_sign(rhs)
    }
}

impl Sub for BigInt {
    type Output = Self;
    fn sub(self, mut rhs: Self) -> Self::Output {
        rhs.sign = rhs.sign.reverse();
        Self::add(self, rhs)
    }
}

impl Mul for BigInt {
    type Output = Self;
    fn mul(self, rhs: Self) -> Self::Output {
        todo!()
    }
}

