#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn it_works() {
        let mut big: BigInt = i32::MAX.into();
        let mut value: u64 = i32::MAX as u64;
        for _ in 0..100 {
            for _ in 0..100 {
                big = big + (i32::MAX).into();
                value += i32::MAX as u64;
            }
            assert_eq!(big.to_string(), format!("{}", value));
        }

        assert_eq!(big.to_string(), format!("{}", value));
    }

    #[test]
    fn subtraction_pos_pos_small_largest() {
        let big_base: BigInt = i128::MAX.into();
        let removed: BigInt = u128::MAX.into();
        let new_value: BigInt = big_base - removed;
        let expected_value: String = format!("-{}", 1u128 << 127);
        assert_eq!(
            new_value.to_string(),
            expected_value,
            "The values must to be equal"
        );
    }

    #[test]
    fn subtraction_pos_pos_largest_small() {
        let big_base: BigInt = u128::MAX.into();
        let removed: BigInt = i128::MAX.into();
        let new_value: BigInt = big_base - removed;
        let expected_value: String = format!("{}", 1u128 << 127);
        assert_eq!(
            new_value.to_string(),
            expected_value,
            "The values must to be equal"
        );
    }
}

